try
{
    $log = @{}

    while ($blah -ne "blah")
    {
        #clear
        
        #### Change these variables
        $OpenCageAPIKey = 'xxxxxxxxxxx'
        $PiStar = "10.0.0.3"
        $photoIcon = 'data:image/jpeg;base64,'
        $nodeRedHttp = 'http://10.0.0.2:1880/pistar-update'
        $sleepTimer = 2
        $ttl = 600
        ###########################
        

        ## Get current Callsign and Talkgroup
        $url = "http://$PiStar/mmdvmhost/lh.php"
        $b = Invoke-WebRequest -Uri $url
        $tables = @($b.ParsedHtml.getElementsByTagName("table"))
        $rows = @($tables.Rows)
        $z = @($rows[1].cells)
        $callsign = (($z.innertext)[2]).split("/")[0]
        $tg = (($z.innertext)[3].split(" "))[1]
        $tgname = ($tglist | ? {$_.talkgroup -eq $tg}).name
        $currentTg = $tg
        $qrzUrl = 'http://www.qrz.com/db/' + $callsign
    
        if ($last.callsign -notlike $callsign -or $callsign.Length -gt 6)
        {
            #write-host -ForegroundColor Green "New callsign: $callsign"
            $post = @{}
            $post.Add('weblink',$qrzUrl)
            $post.Add('callsign',$callsign)
            $post.Add('ttl',$ttl)
            $post.Add('TG',$tg)
            $post.Add('TG Name',$tgname)

            #Query RadioID for Name & Location
            $radioIdUrl = "https://www.radioid.net/api/dmr/user/?callsign=$callsign"
            $radioid = Invoke-RestMethod -Uri $radioIdUrl
    
            #pull out city,state,country variables
            $city = $radioid.results[0].city
            $state = $radioid.results[0].State
            $country = $radioid.results[0].Country
    
            $post.add("name",($radioid.results[0].fname + " " + $radioid.results[0].surname))
            $post.add("City",$radioid.results[0].city)
            $post.add("State",$radioid.results[0].State)
            $post.add("Country",$radioid.results[0].Country)
            $post.add("DMR ID",$radioid.results[0].id)
            $post.add("label", ($post.callsign + " - " + $post.name + " - TG: " + $post.tg))

            $now = get-date
            write-host $now " - " $callsign " - " $post.name  " - TG: $tg - $tgname" 

            if ($log.$callsign.Length -gt 1)
            {
                $indb = 1
                $post = $log.$callsign | convertfrom-json
                $call = $post.callsign
                #write-host "$call has already been queried."
            }
            else{$indb = 0}

            if ((($city+$state+$Country).Length -gt 0 -AND $last.callsign -notlike $post.callsign) -and $indb -ne 1)
            {
                #Get GEO Info
                $geoUrl = "https://api.opencagedata.com/geocode/v1/json?q=$city+$state+$country&key=$OpenCageAPIKey"
                $geoData = Invoke-RestMethod -Uri $geoUrl -Method Get
                #write-host -ForegroundColor Yellow "API Queries Remaining: " $geoData.rate.remaining
                $apilimit = $geoData.rate.remaining
                $host.ui.RawUI.WindowTitle = “Pi-Star Mapper - API Limit: $apilimit”
                $geo = ($geoData.results | Sort-Object confidence)[0]
                $post.Add("lat",$geo.geometry.lat)
                $post.Add("lon",$geo.geometry.lng)
                $post.popped = $true

                #Grab Photo URL
                $photoUrl = ((invoke-webrequest -uri $qrzURL).links | ?{$_.class -like 'fancybox'}).href

                #Check to see if photo exists on QRZ.com, if not file with $photoIcon
                if ($photoUrl.Length -lt 5)
                {
                    #write-host -ForegroundColor Yellow "Photo not found."
                    $photoUrl = $photoIcon
                }
                $post.Add("photoUrl",$photoUrl)
            }
        }


        if ($last.callsign -notlike $post.callsign -and ($last.popped.length -ne 0))
        {
            $last.popped = $false
            #write-host -ForegroundColor Green "Updating Map."
            $send = Invoke-RestMethod -uri $nodeRedHttp -Method Post -ContentType 'application/json' -Body ($last| convertto-json)
            $send = Invoke-RestMethod -uri $nodeRedHttp -Method Post -ContentType 'application/json' -Body ($post| convertto-json)
        }
        else{
            #write-host -ForegroundColor Yellow "Same callsign: $callsign"
        }

        try
        {
            $add1 = $log.Add($callsign,($post|convertto-json))
        
        }
        catch{}

        $last = $post
        sleep $sleepTimer

        #$post
        # write-host -ForegroundColor Magenta "`nCallsigns in memory: " $log.count
        $host.ui.RawUI.WindowTitle = “Pi-Star Mapper  -  API Limit: $apilimit  -  Callsigns in Memory: ” + $log.Count
        sleep $sleepTimer
    }
}

finally
{
    write-host -ForegroundColor Green "`n`n...73!`n"
}